func compressString(s string) []byte {
	var b bytes.Buffer
	gz := gzip.NewWriter(&b)
	if _, err := gz.Write([]byte(s)); err != nil {
		log.Fatalf("Failed to compress string: %v", err)
	}
	if err := gz.Close(); err != nil {
		log.Fatalf("Failed to close gzip writer: %v", err)
	}
	//fmt.Println(b.String())
	return b.Bytes()
}

func decompressString(b []byte) string {
	r, err := gzip.NewReader(bytes.NewBuffer(b))
	if err != nil {
		log.Fatalf("Failed to create gzip reader: %v", err)
	}
	defer r.Close()
	data, err := ioutil.ReadAll(r)
	if err != nil {
		log.Fatalf("Failed to decompress string: %v", err)
	}
	return string(data)
}

